using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using System.Diagnostics;
using System.Text;
using WebSiteFrame.Models;

namespace WebSiteFrame.Controllers
{
    [Authorize]
    public class HomeController : Controller
    {
        private readonly ILogger<HomeController> _logger;

        public HomeController(ILogger<HomeController> logger)
        {
            _logger = logger;
        }

        public IActionResult Index()
        {
            return View();
        }

        public IActionResult About()
        {
            ViewData["Message"] = "Your application description page.";

            var abouts = new AboutModel[] {new AboutModel(1, "Name A", "Position name", "Description", Encoding.ASCII.GetBytes("data:image/jpeg;base64,")),
            new AboutModel(2, "Name B", "Position name", "Description", Encoding.ASCII.GetBytes("data:image/jpeg;base64,")),
            new AboutModel(3, "Name C", "Position name", "Description", Encoding.ASCII.GetBytes("data:image/jpeg;base64,")),
            new AboutModel(4, "Name D", "Position name", "Description", Encoding.ASCII.GetBytes("data:image/jpeg;base64,"))};

            return View(abouts);
        }

        public IActionResult Privacy()
        {
            return View();
        }

        [AllowAnonymous]
        [ResponseCache(Duration = 0, Location = ResponseCacheLocation.None, NoStore = true)]
        public IActionResult Error()
        {
            return View(new ErrorViewModel { RequestId = Activity.Current?.Id ?? HttpContext.TraceIdentifier });
        }
    }
}
